        public static void LeafPileSelf()
        {
            if (!ControllerInputPoller.instance.rightGrab)
                return;

            Vector3 pos = GorillaLocomotion.GTPlayer.Instance.RightHand.controllerTransform.position;
            Vector3 vel = GorillaLocomotion.GTPlayer.Instance.RightHand.controllerTransform.forward
                          * Time.deltaTime * 1000f;

            VRRig myRig = GorillaTagger.Instance.offlineVRRig;
            if (myRig == null) return;

            foreach (Transform t in myRig.GetComponentsInChildren<Transform>(true))
            {
                string n = t.name.ToLower();
                if (!n.Contains("leaf") || !n.Contains("pile"))
                    continue;

                GameObject go = t.gameObject;

                Rigidbody rb = go.GetComponent<Rigidbody>();
                if (rb == null)
                {
                    rb = go.AddComponent<Rigidbody>();
                    rb.useGravity = true;
                    rb.mass = 0.25f;
                    rb.drag = 0.2f;
                    rb.angularDrag = 0.15f;
                }

                go.transform.position = pos;
                rb.velocity = vel;
                rb.angularVelocity = Random.insideUnitSphere * 5f;
            }
        }

