        public static void SavePreferences()
        {
            string directoryPath = "Spider";
            string filePath = Path.Combine(directoryPath, "Spider_Pref.txt");
            Directory.CreateDirectory(directoryPath);
            File.WriteAllText(filePath, GetPreferencesText());
        }

        public static void LoadPreferences()
        {
            string directoryPath = "Spider";
            string preferencesFilePath = Path.Combine(directoryPath, "Spider_Pref.txt");

            try
            {
                if (!Directory.Exists(directoryPath))
                {
                    UnityEngine.Debug.LogWarning("Preferences directory does not exist.");
                    return;
                }

                if (File.Exists(preferencesFilePath))
                {
                    string preferencesText = File.ReadAllText(preferencesFilePath);
                    LoadPreferencesFromText(preferencesText);
                }
                else
                {
                    UnityEngine.Debug.LogWarning("No preferences file found to load.");
                }
            }
            catch (Exception e)
            {
                UnityEngine.Debug.LogError($"Error loading preferences: {e.Message}");
            }
        }

        private static float gripCooldown = 0f;

        public static void GripTurnPages()
        {
            if (StupidTemplate.Menu.Main.menu == null)
                return;

            if (UnityEngine.Time.time < gripCooldown)
                return;

            bool rightGrip = ControllerInputPoller.instance.rightControllerGripFloat > 0.5f;
            bool leftGrip = ControllerInputPoller.instance.leftControllerGripFloat > 0.5f;

            var buttonArray = StupidTemplate.Menu.Buttons.buttons;
            int currentCat = StupidTemplate.Menu.Main.currentCategory;
            int perPage = StupidTemplate.Settings.buttonsPerPage;

            int lastPg = ((buttonArray[currentCat].Length + perPage - 1) / perPage) - 1;

            if (rightGrip)
            {
                StupidTemplate.Menu.Main.pageNumber++;
                if (StupidTemplate.Menu.Main.pageNumber > lastPg)
                    StupidTemplate.Menu.Main.pageNumber = 0;

                StupidTemplate.Menu.Main.RecreateMenu();
                GorillaTagger.Instance.StartVibration(true, 0.25f, 0.15f);
                gripCooldown = UnityEngine.Time.time + 0.25f;
            }

            if (leftGrip)
            {
                StupidTemplate.Menu.Main.pageNumber--;
                if (StupidTemplate.Menu.Main.pageNumber < 0)
                    StupidTemplate.Menu.Main.pageNumber = lastPg;

                StupidTemplate.Menu.Main.RecreateMenu();
                GorillaTagger.Instance.StartVibration(false, 0.25f, 0.15f);
                gripCooldown = UnityEngine.Time.time + 0.25f;
            }
        }

