        private static GameObject CreateStickyPlatform(Transform handTransform)
        {
            GameObject platform = GameObject.CreatePrimitive(PrimitiveType.Cube);
            Renderer renderer = platform.GetComponent<Renderer>();
            renderer.material.shader = Shader.Find("GorillaTag/UberShader");
            renderer.material.color = Settings.MenuColor;

            platform.transform.localScale = scale3;
            platform.transform.position = handTransform.position + new Vector3(0f, -0.01f, 0f);
            platform.transform.rotation = handTransform.rotation;

            Main.FixStickyColliders(platform);

            Rigidbody rb = platform.AddComponent<Rigidbody>();
            rb.isKinematic = true;

            return platform;
        }

