        public static void WASDFly()
        {
            GorillaLocomotion.GTPlayer.Instance.GetComponent<Rigidbody>().velocity = Vector3.zero;
            Transform transform = GorillaTagger.Instance.mainCamera.transform;
            float num = 5.7f * Time.deltaTime;

            if (UnityInput.Current.GetKey(KeyCode.LeftShift) || UnityInput.Current.GetKey(KeyCode.RightShift))
            {
                num *= 2f;
            }

            if (UnityInput.Current.GetKey(KeyCode.W))
            {
                transform.position += transform.forward * num;
            }

            if (UnityInput.Current.GetKey(KeyCode.S))
            {
                transform.position += transform.forward * -1f * num;
            }

            if (UnityInput.Current.GetKey(KeyCode.A))
            {
                transform.position += transform.right * -1f * num;
            }

            if (UnityInput.Current.GetKey(KeyCode.D))
            {
                transform.position += transform.right * num;
            }

            if (UnityInput.Current.GetKey(KeyCode.Space))
            {
                transform.position += transform.up * num;
            }

            if (UnityInput.Current.GetKey(KeyCode.LeftControl) || UnityInput.Current.GetKey(KeyCode.RightControl))
            {
                transform.position += transform.up * -1f * num;
            }

            if (UnityInput.Current.GetMouseButton(1))
            {
                Vector3 vector = UnityInput.Current.mousePosition - lastmouseposition;
                float y = transform.localEulerAngles.y + vector.x * 0.3f;
                float x = transform.localEulerAngles.x - vector.y * 0.3f;
                transform.localEulerAngles = new Vector3(x, y, 0f);
            }

            lastmouseposition = UnityInput.Current.mousePosition;
        }

