                    foreach (VRRig rig in GorillaParent.instance.vrrigs)
                    {
                        if (rig != GorillaTagger.Instance.offlineVRRig)
                        {
                            float rightDistance = Vector3.Distance(rig.rightHandTransform.position, line.reportButton.transform.position);
