        public static float tagSelfTimer = 0f;

        public static void TagSelf()
        {
            foreach (GorillaTagManager gorillaTagManager in Object.FindObjectsOfType<GorillaTagManager>())
            {
                bool isMaster = PhotonNetwork.LocalPlayer.IsMasterClient;
                bool iAmInfected = gorillaTagManager.currentInfected.Contains(PhotonNetwork.LocalPlayer);
                if (!isMaster)
                {
                    if (!iAmInfected)
                    {
                        GorillaTagger.Instance.offlineVRRig.enabled = false;

                        foreach (VRRig rig in GorillaParent.instance.vrrigs)
                        {
                            if (rig.mainSkin.material.name.Contains("fected"))
                            {
                                GorillaTagger.Instance.offlineVRRig.transform.position =
                                    rig.rightHandTransform.position;

                                tagSelfTimer = Time.time + 0.35f;
                                break;
                            }
                        }
                    }
                    else
                    {
                        GorillaTagger.Instance.offlineVRRig.enabled = true;
                        GorillaTagger.Instance.offlineVRRig.transform.position =
                            GorillaTagger.Instance.myVRRig.transform.position;

                        Main.GetIndex("Tag Self").enabled = false;
                    }
                    if (!iAmInfected && Time.time > tagSelfTimer && tagSelfTimer != 0f)
                    {
                        GorillaTagger.Instance.offlineVRRig.enabled = true;
                        tagSelfTimer = 0f;
                    }
                }
                else
                {
                    if (!iAmInfected)
                        gorillaTagManager.currentInfected.Add(PhotonNetwork.LocalPlayer);

                    GorillaTagger.Instance.offlineVRRig.enabled = true;
                    Main.GetIndex("Tag Self").enabled = false;
                }
            }
        }

