        public static void SilentAim()
        {
            if (Codes.SilentAimDelay > Time.time) return;
            Codes.SilentAimDelay = Time.time + 0.05f;

            Player targetPlayer = RigManager.GetRandomPlayer(false);
            if (targetPlayer == null) return;

            VRRig targetRig = RigManager.GetVRRigFromPlayer(targetPlayer);
            if (targetRig == null) return;

            VRRig myRig = GorillaTagger.Instance.offlineVRRig;
            if (myRig == null) return;

            bool isOpponent = (targetRig.mainSkin.material.name.Contains("orangealive") && myRig.mainSkin.material.name.Contains("blue")) ||
                              (targetRig.mainSkin.material.name.Contains("bluealive") && myRig.mainSkin.material.name.Contains("orange"));
            if (!isOpponent) return;

            GameObject pools = GameObject.Find("Environment Objects/05Maze_PersistentObjects/GlobalObjectPools");
            if (pools == null) return;

            foreach (SlingshotProjectile projectile in pools.GetComponentsInChildren<SlingshotProjectile>())
            {
                if (projectile.projectileOwner == (NetPlayer)PhotonNetwork.LocalPlayer)
                {
                    projectile.gameObject.transform.position = targetRig.transform.position;
                    Codes.FlushRpcs();
                }
            }
        }

