        public static void JoinRedTeam()
        {
            foreach (GorillaPaintbrawlManager manager in Object.FindObjectsOfType<GorillaPaintbrawlManager>())
            {
                if (!NetworkSystem.Instance.IsMasterClient)
                    return;

                NetPlayer localPlayer = NetworkSystem.Instance.LocalPlayer;
                manager.RandomizeTeams();
                manager.playerStatusDict[localPlayer.ActorNumber] = GorillaPaintbrawlManager.PaintbrawlStatus.RedTeam;
                manager.AddPlayerToCorrectTeam(localPlayer);
            }
        }

