        public static void AntiTag3()
        {
            VRRig localRig = GorillaTagger.Instance.offlineVRRig;

            if (!localRig.mainSkin.material.name.Contains("fected"))
            {
                Vector3 escapeDirection = Vector3.zero;
                float minDistance = float.MaxValue;

                foreach (VRRig vrrig in GorillaParent.instance.vrrigs)
                {
                    if (vrrig.mainSkin.material.name.Contains("fected"))
                    {
                        float distance = Vector3.Distance(vrrig.transform.position, localRig.transform.position);
                        if (distance <= 7f && distance < minDistance)
                        {
                            minDistance = distance;

                            escapeDirection = (localRig.transform.position - vrrig.transform.position).normalized;
                        }
                    }
                }

                if (minDistance <= 7f && escapeDirection != Vector3.zero)
                {
                    localRig.enabled = false;

                    Vector3 newPos = localRig.transform.position + escapeDirection * 7f;

                    newPos.y = Mathf.Max(newPos.y, 1f);

                    localRig.transform.position = newPos;

                    localRig.enabled = true;
                }
            }
        }

