        public static void StartWhackAMole()
        {
            WhackAMole[] machines = UnityEngine.Object.FindObjectsOfType<WhackAMole>();

            foreach (WhackAMole machine in machines)
            {
                if (machine == null || !machine.isActiveAndEnabled)
                    continue;

                WhackAMole.GameState state = WhackAMole.GameState.Off;

                try
                {
                    state = machine.Data.CurrentState;
                }
                catch (Exception e)
                {
                    Debug.LogWarning($"[StartAllWhackAMole] Failed to read Data from machine '{machine.machineId}': {e.Message}");
                }

                if (state == WhackAMole.GameState.Off || state == WhackAMole.GameState.TimesUp)
                {
                    try
                    {

                        machine.OnStartButtonPressed();
                        Debug.Log($"[StartAllWhackAMole] Started machine '{machine.machineId}' in state {state}.");
                    }
                    catch (Exception e)
                    {
                        Debug.LogError($"[StartAllWhackAMole] Error starting machine '{machine.machineId}': {e}");
                    }
                }
            }
        }

