        public static void OculusReportMenu1()
        {

            bool rightGrip = ControllerInputPoller.instance.rightControllerGripFloat == 1f;

            if (rightGrip && !reportMenuToggle)
            {
                var misc = GetObject("Miscellaneous Scripts");
                if (misc == null) return;

                var metaObj = misc.transform.Find("MetaReporting")?.gameObject;
                if (metaObj == null) return;

                var meta = metaObj.GetComponent<GorillaMetaReport>();
                if (meta == null) return;

                metaObj.SetActive(true);
                meta.enabled = true;

                var method = typeof(GorillaMetaReport).GetMethod(
                    "StartOverlay",
                    BindingFlags.NonPublic | BindingFlags.Instance
                );

                if (method != null)
                {
                    method.Invoke(meta, new object[] { false });
                }
                else
                {
                    Debug.LogError("StartOverlay not found!");
                }
            }

            reportMenuToggle = rightGrip;
        }
        private static readonly Dictionary<string, GameObject> objectPool = new Dictionary<string, GameObject>();
        public static GameObject GetObject(string find)
        {
            if (objectPool.TryGetValue(find, out GameObject go))
                return go;

            GameObject tgo = GameObject.Find(find);
            if (tgo != null)
                objectPool.Add(find, tgo);

            return tgo;
        }

