        public static void LeafRain()
        {
            if (!ControllerInputPoller.instance.rightGrab)
                return;

            Vector3 playerPos = GorillaLocomotion.GTPlayer.Instance.transform.position;
            GameObject[] all = GameObject.FindObjectsOfType<GameObject>();

            foreach (GameObject go in all)
            {
                if (go == null || !go.activeInHierarchy)
                    continue;

                string n = go.name.ToLower();
                if (!n.Contains("leaf") || !n.Contains("pile"))
                    continue;

                go.isStatic = false;

                Rigidbody rb = go.GetComponent<Rigidbody>();
                if (rb == null) rb = go.AddComponent<Rigidbody>();
                rb.useGravity = true;
                rb.mass = 0.15f;

                Vector3 pos = go.transform.position;
                pos.y = playerPos.y + 30f;
                go.transform.position = pos;
                rb.velocity = Vector3.down * 12f;

                for (int i = 0; i < 50; i++)
                {
                    GameObject clone = UnityEngine.Object.Instantiate(go);
                    clone.isStatic = false;

                    Rigidbody rb2 = clone.GetComponent<Rigidbody>();
                    if (rb2 == null) rb2 = clone.AddComponent<Rigidbody>();
                    rb2.useGravity = true;
                    rb2.mass = 0.15f;

                    Vector3 rainPos = playerPos;
                    rainPos.x += UnityEngine.Random.Range(-10f, 10f);
                    rainPos.z += UnityEngine.Random.Range(-10f, 10f);
                    rainPos.y += UnityEngine.Random.Range(20f, 40f);

                    clone.transform.position = rainPos;
                    rb2.velocity = Vector3.down * UnityEngine.Random.Range(8f, 15f);
                }

                return;
            }
        }

