        public static void RainbowRope()
        {
            float baseHue = (Time.time * 0.2f) % 1f;
            GorillaRopeSwing[] ropes = Object.FindObjectsOfType<GorillaRopeSwing>();

            foreach (var rope in ropes)
            {
                Renderer[] rends = rope.GetComponentsInChildren<Renderer>(true);
                int len = rends.Length;

                for (int i = 0; i < len; i++)
                {
                    Renderer r = rends[i];
                    float h = Mathf.Repeat(baseHue + (i / (float)len), 1f);
                    Color c = Color.HSVToRGB(h, 1f, 1f);

                    r.GetPropertyBlock(mpb);

                    var mat = r.sharedMaterial;
                    if (mat != null)
                    {
                        if (mat.HasProperty("_BaseColor")) mpb.SetColor("_BaseColor", c);
                        if (mat.HasProperty("_Color")) mpb.SetColor("_Color", c);

                        if (mat.HasProperty("_EmissionColor"))
                        {
                            mpb.SetColor("_EmissionColor", c);
                            mat.EnableKeyword("_EMISSION");
                        }
                    }
                    else
                    {
                        mpb.SetColor("_Color", c);
                        mpb.SetColor("_EmissionColor", c);
                    }

                    r.SetPropertyBlock(mpb);
                }
            }
        }

