        public static void RopeFreeze()
        {
            if (ControllerInputPoller.instance.rightGrab)
            {
                if (Time.time > Codes.RopeDelay + 0.1f)
                {
                    Codes.RopeDelay = Time.time;

                    RopeSwingManager manager = RopeSwingManager.instance;

                    if (manager != null)
                    {
                        var ropes = Traverse
                            .Create(manager)
                            .Field("ropes")
                            .GetValue<Dictionary<int, GorillaRopeSwing>>();

                        if (ropes != null)
                        {
                            foreach (var rope in ropes)
                            {
                                manager.SendSetVelocity_RPC(rope.Key, 1, Vector3.zero, true);
                            }
                        }
                    }
                }
            }
        }

        public static void RopesSpaz()
        {
            bool run = true;

            if (ControllerInputPoller.instance.rightGrab)
                run = false;

            if (Time.time <= RopeDelay + 0.1f)
                run = false;

            RopeSwingManager manager = RopeSwingManager.instance;
            if (manager == null)
                run = false;

            Dictionary<int, GorillaRopeSwing> ropes = null;
            if (run)
                ropes = Traverse.Create(manager).Field("ropes").GetValue<Dictionary<int, GorillaRopeSwing>>();

            if (ropes == null || ropes.Count == 0)
                run = false;

            if (run)
            {
                RopeDelay = Time.time;

                foreach (var rope in ropes)
                {
                    Vector3 velocity = new Vector3(
                        Random.Range(-300f, 300f),
                        0f,
                        Random.Range(-300f, 300f)
                    );

                    manager.photonView.RPC(
                        "SetVelocity",
                        RpcTarget.All,
                        rope.Key,
                        1,
                        velocity,
                        true
                    );
                }
            }
        }


