        public static void BetaFireProjectile(string projectileName, Vector3 position, Vector3 velocity, Color color, bool noDelay = false)
        {
            if (!noDelay && projDebounceType > 0f && Time.time < projDebounce)
                return;

            if (velocity.magnitude > 9999f)
                velocity = velocity.normalized * 9999f;

            if (color.a <= 0f)
                color.a = 1f;

            SnowballThrowable throwable = GetProjectile(projectileName);
            if (throwable == null)
            {
                Debug.LogError("[Projectile] Throwable not found: " + projectileName);
                return;
            }

            GameObject prefab = throwable.randomModelSelection
                ? throwable.localModels[0].GetProjectilePrefab()
                : throwable.projectilePrefab;

            if (prefab == null)
            {
                Debug.LogError("[Projectile] Prefab missing for: " + projectileName);
                return;
            }

            SlingshotProjectile proj = ObjectPools.instance.Instantiate(prefab)
                .GetComponent<SlingshotProjectile>();

            if (proj == null)
            {
                Debug.LogError("[Projectile] SlingshotProjectile component missing");
                return;
            }

            float scale = throwable.transform.lossyScale.x;
            var localPlayer = NetworkSystem.Instance.LocalPlayer;

            MethodInfo launchMethod = typeof(SlingshotProjectile).GetMethod(
                "Launch",
                BindingFlags.Public | BindingFlags.NonPublic | BindingFlags.Instance
            );

            if (launchMethod == null)
            {
                Debug.LogError("[Projectile] Launch() method NOT found!");
                return;
            }

            ParameterInfo[] parms = launchMethod.GetParameters();
            object[] args = new object[parms.Length];

            for (int i = 0; i < parms.Length; i++)
            {
                var param = parms[i];
                Type t = param.ParameterType;
                string name = (param.Name ?? string.Empty).ToLowerInvariant();

                if (t == typeof(Vector3))
                {
                    if (name.Contains("pos"))
                        args[i] = position;
                    else if (name.Contains("vel"))
                        args[i] = velocity;
                    else
                        args[i] = Vector3.zero;
                }
                else if (!t.IsValueType && name.Contains("player"))
                {
                    args[i] = localPlayer;
                }
                else if (t == typeof(bool))
                {
                    if (name.Contains("blue") || name.Contains("orange"))
                    {
                        args[i] = false;
                    }
                    else if (name.Contains("override"))
                    {
                        args[i] = true;
                    }
                    else
                    {
                        args[i] = false;
                    }
                }
                else if (t == typeof(int))
                {
                    args[i] = 0;
                }
                else if (t == typeof(float))
                {
                    args[i] = scale;
                }
                else if (t == typeof(Color))
                {
                    args[i] = color;
                }
                else
                {
                    args[i] = Type.Missing;
                }
            }

            try
            {
                launchMethod.Invoke(proj, args);
            }
            catch (Exception e)
            {
                Debug.LogError("[Projectile] Launch invoke failed: " + e);
            }

            try
            {
                proj.ApplyTeamModelAndColor(false, false, true, color);
            }
            catch
            {
            }

            string eventName;
            if (!string.IsNullOrEmpty(throwable.throwEventName))
                eventName = throwable.throwEventName;
            else if (throwable.projectilePrefab != null)
                eventName = throwable.projectilePrefab.name;
            else
                eventName = projectileName;

            PlayerGameEvents.LaunchedProjectile(eventName);
            FlushRpcs();

            if (projDebounceType > 0f && !noDelay)
            {
                projDebounce = Time.time + projDebounceType + 0.05f;
            }
        }

        public static SnowballThrowable GetProjectile(string projectileName)
        {
            if (snowballDict == null)
            {
                if (!CosmeticsV2Spawner_Dirty.allPartsInstantiated)
                    return null;

                snowballDict = new Dictionary<string, SnowballThrowable>();

                foreach (SnowballMaker Maker in new[] { SnowballMaker.leftHandInstance, SnowballMaker.rightHandInstance })
                {
                    foreach (SnowballThrowable Throwable in Maker.snowballs)
                    {
                        try
                        {
                            snowballDict.Add(Throwable.transform.parent.gameObject.name, Throwable);
                        }
                        catch { }
                    }
                }
            }

            projectileName += "(Clone)";

            if (snowballDict != null && snowballDict.TryGetValue(projectileName, out var projectile))
                return projectile;
            return null;
        }

