        public static void LoadPreferencesFromText(string text)
        {
            string[] lines = text.Split('\n');
            if (lines.Length >= 1)
            {
                string[] activeButtons = lines[0].Split(new string[] { ";;" }, StringSplitOptions.None);
                foreach (ButtonInfo[] buttonList in Buttons.buttons)
                {
                    foreach (ButtonInfo button in buttonList)
                    {
                        if (activeButtons.Contains(button.buttonText))
                        {
                            button.enabled = true;
                        }
                        else if (button.buttonText != "Save Preferences")
                        {
                            button.enabled = false;
                        }
                    }
                }
            }
            if (lines.Length >= 2)
            {
                string encrypt = lines[1];
            }
        }

