                if (!once_left_false && jump_left_local != null)
                {
                    UnityEngine.Object.Destroy(jump_left_local.GetComponent<Collider>());
                    Rigidbody comp = jump_left_local.AddComponent(typeof(Rigidbody)) as Rigidbody;
